import { useEffect, useState } from "react"
import {Link,Route} from 'react-router-dom'
import './Hot.css'

function Hot (props) {
    const [hotList, setHotList]=useState([]);
    const [selectHot,setSelectHot] = useState(null);
    let photo = "https://www.yuzubakes.com/sites/yuzubakes.com/files/styles/yct_adaptive_ls_scale_720/public/pinterest-yuzu-bakes.jpg"


    const makeHotListCall = () =>{
        fetch("https://api.sampleapis.com/coffee/hot")
        .then((res) => res.json())
        .then((data)=>{
            setHotList(data)
        })
    }
    useEffect(()=> {
        makeHotListCall()
    },[])

    const handleHotClick = (title) =>{
        setSelectHot(title)
    }


   const hotDrinkList = hotList.map(hot =>{
    return <p onClick= {()=> handleHotClick(hot.id)}><Link to = {"/hot/"+ hot.id}>{hot.title}</Link></p>
})
    return <div className ="Hot">
        <h2>Hot Drink</h2>
        <img src={photo} alt="hot coffee image"/>
        {hotDrinkList}
        {/* <HotDescription addDrinkToCart={props.addDrinkToCart}/> */}
        </div>
}

export default Hot
