import { useEffect, useState } from "react"


function HotDescription (props) {
    const [hotCoffeeDetail,setHotCoffeeDetail]=useState('')

    // const selectedCoffee = props.hotDrinkList && props.hotDrinkList.find(coffee  => coffee.id === props.id)
    const makeHotListCall = () =>{
        fetch(`https://api.sampleapis.com/coffee/hot/${props.match.params.id}`)
        .then((res) => res.json())
        .then((data)=>{
            console.log(data)
        })
    }
    useEffect(()=> {
        makeHotListCall()
    },[])

    const coffeeDetail = hotCoffeeDetail.map(coffee =>{
        return (
        <div>
        <h1>{coffee.title}</h1>
        <p>{coffee.description}</p>

        </div>)
    })


    return <div>
            {coffeeDetail}
        </div>
}

export default HotDescription
