function HotDescription (props) {

    console.log(props)

    const hotCoffee = props.HotDrinkList.map(coffee =>{
        return (
        <div>
            <p>{coffee.description}</p>
            <p>{coffee.ingredients}</p>
         </div>
                    )
    })

    let coffeeDetail = ''
    if(props.title){
        coffeeDetail = 
        <div>
            <h3>{props.title}</h3>
            {hotCoffee}
            <button onClick={()=>props.addCoffee()}>Add to Cart</button>
        </div>
    }




    return <div>
        Description
            {coffeeDetail}
        </div>
}

export default HotDescription
