import { useEffect, useState } from "react"
import HotDescription from "../Description/HotDescription";
import {Link,Route} from 'react-router-dom'

function Hot (props) {
    const [hotList, setHotList]=useState([]);
    const [selectHot,setSelectHot] = useState(null);



    const makeHotListCall = () =>{
        fetch("https://api.sampleapis.com/coffee/hot")
        .then((res) => res.json())
        .then((data)=>{
            console.log(data)
            setHotList(data)
        })
    }
    useEffect(()=> {
        makeHotListCall()
    },[])

    const handleHotClick = (title) =>{
        setSelectHot(title)
    }

    // <Link to = {"/hot/"+ hot.id}> </Link>
   const hotDrinkList = hotList.map(hot =>{
    return <p onClick= {()=> handleHotClick(hot.id)}>{hot.title}</p>
})
    console.log(props)
    return <div>
        <h2>Hot Drink</h2>
       {selectHot ?<HotDescription id={selectHot} hotDrinkList = {hotList} addDrinkToCart={props.addDrinkToCart}/>
        : hotDrinkList
        }
        </div>
}

export default Hot


